import React, { useRef } from 'react';
// import FunnelGraph from 'funnel-graph-js';
import { Funnel} from 'funnel-graph-js';

interface FunnelGraphProps {
    data: {label: string, value: number}[];
    width: number;
    height: number;
    bottomWidth?: number;
    bottomPinch?: number;
    sort?: boolean;
    label?: { fontSize: string; fill: string; }
    color?: {
        scale: string[],
        start: string,
        end: string
    }
}

class funnel extends React.Component {
    render() {
        return (
            <div>
                <FunnelGraph
                    data={[
                        { label: 'Step 1', value: 100 },
                        { label: 'Step 2', value: 80 },
                        { label: 'Step 3', value: 60 },
                        { label: 'Step 4', value: 40 },
                        { label: 'Step 5', value: 20 },
                    ]}
                    width={500}
                    height={500}
                />
            </div>
        );
    }
}

const FunnelGraph: React.FC<FunnelGraphProps> = (props) => {
    const ref = useRef(null);

    React.useEffect(() => {
        const graph = new Funnel({
            container: ref.current,
            data: props.data,
            width: props.width,
            height: props.height,
            bottomWidth: props.bottomWidth,
            bottomPinch: props.bottomPinch,
            sort: props.sort,
            label: props.label,
            color: props.color
        });

        graph.draw();
    }, [props.data, props.width, props.height, props.bottomWidth, props.bottomPinch, props.sort, props.label, props.color]);

    return (
        <div className="funnel" ref={ref}></div>
    );
}

export default FunnelGraph;
