import * as FunnelGraphJs from 'funnel-graph-js';
import React from 'react';

interface FunnelGraphProps {
    data: {label: string, value: number}[];
    width: number;
    height: number;
    options?: any; //options that the library accepts
}


const FunnelGraph: React.FC<FunnelGraphProps> = (props) => {
    const containerRef = React.useRef<HTMLDivElement>(null);

    React.useEffect(() => {
        const funnelGraph = new FunnelGraphJs.FunnelGraph({
            container: containerRef.current,
            data: props.data,
            width: props.width,
            height: props.height,
            options: props.options,
        });
        funnelGraph.draw();
    }, [props.data, props.width, props.height, props.options]);

    return (
        <div ref={containerRef}></div>
    );
}
