import React, { useEffect } from "react";
import * as FunnelGraphJs from "funnel-graph-js";

interface FunnelGraphProps {
    data: {
        labels: string[],
        subLabels: string[],
        colors: any[],
        values: any[]
    }
    options?: FunnelGraphJs.FunnelGraphOptions
}

const App: React.FC<FunnelGraphProps> = (props) => {
    const containerRef = React.useRef<HTMLDivElement>(null);
    const funnelGraph = React.useRef<FunnelGraphJs.FunnelGraph>();

    useEffect(() => {
        const options = {
            ...props.options,
            container: containerRef.current,
            data: props.data,
            width: 1000,
            height: 350,
            subLabelValue: "values"
        }
        funnelGraph.current = new FunnelGraphJs.FunnelGraph(options);
        funnelGraph.current.draw();
    }, [props.data, props.options]);

    return (
        <div ref={containerRef} className="App"></div>
    );
}

export default App;
