import { drawFunnel } from 'funnel-graph-js';
import { FunnelGraph as ExternalFunnelGraph } from 'funnel-graph-js';
import React from 'react';
import { useEffect } from 'react';

const FunnelGraph: React.FC = () => {
    
    const data = {
      labels: ['Step 1', 'Step 2', 'Step 3', 'Step 4', 'Step 5'],
      values: [100, 80, 60, 40, 20]
    };
  
    useEffect(() => {
      drawFunnel({
        container: '.funnel',
        gradientDirection: 'horizontal',
        data: data,
        displayPercent: true,
        direction: 'horizontal'
      });
    }, []);
  
    return <div className="funnel"></div>;
  }
  

export default FunnelGraph;
