import React, { useRef } from 'react';
import FunnelGraph from 'funnel-graph-js';


const Fu: React.FC = () => {
  const containerRef = useRef(null);

  const data = [
    { label: 'Step 1', value: 100 },
    { label: 'Step 2', value: 80 },
    { label: 'Step 3', value: 60 },
    { label: 'Step 4', value: 40 },
    { label: 'Step 5', value: 20 },
  ];

  const options = {
    width: 600,
    height: 400,
    bottomWidth: 1 / 3,
    bottomPinch: 1 / 3,
    isCurved: true,
    curveHeight: 20,
    fillType: 'solid',
    isInverted: false,
  };

  React.useEffect(() => {
    if (containerRef.current) {
      const chart = new FunnelGraph(containerRef.current, data, options);
      chart.draw();
    }
  }, []);

  return <div ref={containerRef} />;
};

export default FunnelGraph;
