const MyFunnelChart: React.FC<Props> = ({data}) => {
  const containerRef = useRef(null);
    const options = {
    width: 600,
    height: 400,
    bottomWidth: 1 / 3,
    bottomPinch: 1 / 3,
    isCurved: true,
    curveHeight: 20,
    fillType: 'solid',
    isInverted: false,
  };

  React.useEffect(() => {
    if (containerRef.current && data) {
      const chart = new FunnelGraph(containerRef.current, data,options);
      chart.draw();
    }
  }, [data]);

  return <div ref={containerRef} />;
};

export default MyFunnelChart;
