import * as FunnelGraphJs from 'funnel-graph-js';
import React, { useRef } from 'react';

interface FunnelGraphProps {
    data: {label: string, value: number}[];
    width: number;
    height: number;
    options?: FunnelGraphJs.FunnelGraphOptions;
}

const FunnelGraph: React.FC<FunnelGraphProps> = (props) => {
    const containerRef = useRef<HTMLDivElement>(null);
    const funnelGraph = useRef<FunnelGraphJs.FunnelGraph>();

    React.useEffect(() => {
        if(funnelGraph.current) {
            funnelGraph.current.update(props.data);
        } else {
            const options = {
                ...props.options,
                container: containerRef.current,
                data: props.data,
                width: props.width,
                height: props.height
            }
            funnelGraph.current = new FunnelGraphJs.FunnelGraph(options);
            funnelGraph.current.draw();
        }
    }, [props.data, props.width, props.height, props.options]);

    return (
        <div ref={containerRef} style={{width: props.width, height: props.height}}></div>
    );
}

export default FunnelGraph;
